/**
 * Search the knowledge base for relevant content
 * Uses keyword matching to find the most relevant paragraphs
 * @param {string} query - User question
 * @param {number} maxResults - Max paragraphs to return
 * @returns {Array} Top matching paragraphs with source info
 */
export function searchRagKnowledge(query, maxResults = 5) {
  const words = query.toLowerCase().split(/\s+/).filter(w => w.length > 2);
  const results = [];

  for (const doc of ragKnowledgeBase) {
    for (const para of doc.paragraphs) {
      const paraLower = para.toLowerCase();
      let score = 0;
      for (const w of words) {
        const matches = (paraLower.match(new RegExp(w, 'g')) || []).length;
        score += matches;
      }
      if (score > 0) {
        results.push({ score, source: doc.source, text: para });
      }
    }
  }

  results.sort((a, b) => b.score - a.score);
  return results.slice(0, maxResults);
}
